import React, { useEffect, useRef, useState } from 'react';
import 'ol/ol.css';
import Map from 'ol/Map';
import View from 'ol/View';
import TileLayer from 'ol/layer/Tile';
import OSM from 'ol/source/OSM';
import VectorLayer from 'ol/layer/Vector';
import VectorSource from 'ol/source/Vector';
import Draw from 'ol/interaction/Draw';
import Overlay from 'ol/Overlay';
import { fromLonLat } from 'ol/proj';
import { getLength } from 'ol/sphere';
import LineString from 'ol/geom/LineString';

const App = () => {
  const [text, setText] = useState('');
  const inputRef = useRef();

  useEffect(() => {
    const vectorSource = new VectorSource();
    const vectorLayer = new VectorLayer({ source: vectorSource });

    const map = new Map({
      target: 'map',
      layers: [
        new TileLayer({ source: new OSM() }),
        vectorLayer,
      ],
      view: new View({
        center: fromLonLat([-95.87, 40.84]),
        zoom: 24
      }),
    });

    const draw = new Draw({
      source: vectorSource,
      type: 'Polygon',
    });
    map.addInteraction(draw);

    let allSegmentOverlays = [];

    function formatLength(line) {
      const lengthMeters = getLength(line);
      const lengthFeet = lengthMeters * 3.28084;
      return lengthFeet > 5280
        ? (lengthFeet / 5280).toFixed(2) + ' mi'
        : lengthFeet.toFixed(2) + ' ft';
    }

    function createSegmentOverlays(coords) {
      const overlays = [];
      for (let i = 1; i < coords.length; i++) {
        const c1 = coords[i - 1];
        const c2 = coords[i];
        const line = new LineString([c1, c2]);
        const length = formatLength(line);

        const midpoint = [
          (c1[0] + c2[0]) / 2,
          (c1[1] + c2[1]) / 2
        ];

        const tooltip = document.createElement('div');
        tooltip.className = 'segment-tooltip';
        tooltip.style.background = 'white';
        tooltip.style.border = '1px solid black';
        tooltip.style.padding = '2px 4px';
        tooltip.style.position = 'relative';
        tooltip.style.whiteSpace = 'nowrap';
        tooltip.innerHTML = length;

        const overlay = new Overlay({
          element: tooltip,
          position: midpoint,
          positioning: 'center-center',
          stopEvent: false,
        });
        map.addOverlay(overlay);
        overlays.push(overlay);
      }
      return overlays;
    }

    let currentSegmentOverlays = [];
    let drawing = false;
    let currentGeom = null;

    draw.on('drawstart', function (evt) {
      drawing = true;
      currentGeom = evt.feature.getGeometry();
      currentSegmentOverlays.forEach(overlay => map.removeOverlay(overlay));
      currentSegmentOverlays = [];

      currentGeom.on('change', function () {
        currentSegmentOverlays.forEach(overlay => map.removeOverlay(overlay));
        currentSegmentOverlays = [];
        const coords = currentGeom.getCoordinates()[0];
        currentSegmentOverlays = createSegmentOverlays(coords);
      });
    });

    draw.on('drawend', function (evt) {
      drawing = false;
      currentGeom = null;
      const coords = evt.feature.getGeometry().getCoordinates()[0];
      const overlays = createSegmentOverlays(coords);
      allSegmentOverlays.push(...overlays);
      currentSegmentOverlays = [];
    });

    function addSegment(direction) {
      if (!drawing || !currentGeom) return;
      // Get the current coordinates array (first ring of the polygon)
      const coords = currentGeom.getCoordinates()[0];
      // The last placed vertex is just before the closing coordinate
      const last = coords[coords.length - 2] || coords[coords.length - 1];
      let dx = 0, dy = 0;
      const offset = 2; // map units

      switch (direction) {
        case 'ArrowUp': dy = offset; break;
        case 'ArrowDown': dy = -offset; break;
        case 'ArrowLeft': dx = -offset; break;
        case 'ArrowRight': dx = offset; break;
        default: return;
      }

      const newPt = [last[0] + dx, last[1] + dy];
      // Insert the new point before the closing coordinate
      const newCoords = [
        ...coords.slice(0, coords.length - 1),
        newPt,
        coords[coords.length - 1]
      ];
      currentGeom.setCoordinates([newCoords]);
    }

    function handleKeyDown(e) {
      if (
        e.key === 'ArrowUp' ||
        e.key === 'ArrowDown' ||
        e.key === 'ArrowLeft' ||
        e.key === 'ArrowRight'
      ) {
        e.preventDefault();
        addSegment(e.key);
      }
    }

    document.addEventListener('keydown', handleKeyDown);

    return () => {
      map.setTarget(null);
      document.removeEventListener('keydown', handleKeyDown);
    };
  }, []);

  return (
    <div>
      <input
        ref={inputRef}
        type="text"
        value={text}
        onChange={e => setText(e.target.value)}
        placeholder="Dimension"
        autoFocus
        style={{
          position: 'absolute',
          top: 20,
          right: 20,
          zIndex: 1000,
          width: 300,
          padding: 8,
          fontSize: 16,
          pointerEvents: 'auto',
        }}
      />
      <div id="map" style={{ width: '100vw', height: '100vh' }}></div>
      <style>
        {`
          .segment-tooltip {
            font-size: 12px;
            pointer-events: none;
          }
        `}
      </style>
    </div>
  );
};

export default App;
