import React, { useEffect, useRef, useState } from 'react';
import './style.css';
import 'ol/ol.css';
import Map from 'ol/Map';
import View from 'ol/View';
import TileLayer from 'ol/layer/Tile';
import OSM from 'ol/source/OSM';
import VectorLayer from 'ol/layer/Vector';
import VectorSource from 'ol/source/Vector';
import Feature from 'ol/Feature';
import Polygon from 'ol/geom/Polygon';
import Point from 'ol/geom/Point';
import Select from 'ol/interaction/Select';
import { fromLonLat } from 'ol/proj';
import ScaleLine from 'ol/control/ScaleLine';

/* map actions */
import FlipHorizButton from './map-actions/FlipHorizontal';
import FlipVertButton from './map-actions/FlipVertical';
import RightClick from './map-actions/RightClick';
import History from './map-helpers/History';

/* map stylings */
import {
  polygonStyle,
  activePolygonStyle,
  selectedPolygonStyle,
  ghostMarkerStyle
} from './ol-styles';

/* measurement helpers */
import {
  clearPolygonOverlays,
  createSegmentOverlays,
  remarkPolygonSegments,
  remarkLineSegments
} from './map-helpers/Measurements';

const OFFSET_REF_DEFAULT = 2.018;

const App = () => {
  const [text, setText] = useState('');
  const [mode, setMode] = useState('draw');
  const [selectedFeature, setSelectedFeature] = useState(null);
  const [contextMenu, setContextMenu] = useState({ visible: false, x: 0, y: 0, type: null, feature: null, index: null });
  const [history, setHistory] = useState([]);
  const inputRef = useRef();
  const mapRef = useRef();
  const vectorSourceRef = useRef();
  const vertexSourceRef = useRef();
  const polygonFeatureRef = useRef();
  const overlaysRef = useRef([]);
  const allOverlaysRef = useRef([]);
  const drawingRef = useRef(false);
  const offsetRef = useRef(OFFSET_REF_DEFAULT);
  const ghostMarkerRef = useRef(null);
  const modeRef = useRef(mode);
  const selectInteractionRef = useRef(null);
  const vectorLayerRef = useRef();

  useEffect(() => {
    modeRef.current = mode;
  }, [mode]);

  useEffect(() => {
    const vectorSource = new VectorSource();
    vectorSourceRef.current = vectorSource;
    const vertexSource = new VectorSource();
    vertexSourceRef.current = vertexSource;

    const vectorLayer = new VectorLayer({
      source: vectorSource,
      style: polygonStyle,
    });
    vectorLayerRef.current = vectorLayer;

    const vertexLayer = new VectorLayer({
      source: vertexSource,
      style: ghostMarkerStyle,
    });

    const map = new Map({
      target: 'map',
      layers: [
        new TileLayer({ source: new OSM() }),
        vectorLayer,
        vertexLayer,
      ],
      view: new View({
        center: fromLonLat([-95.87, 40.84]),
        zoom: 24,
      }),
    });
    map.addControl(new ScaleLine({ units: 'imperial' }));
    mapRef.current = map;

    // Expose remarkPolygonSegments for use in handlers
    mapRef.current.remarkPolygonSegments = (feature) => remarkPolygonSegments(feature, mapRef);

    // --- GHOST MARKER LOGIC ---
    map.on('pointermove', evt => {
      if (modeRef.current === 'draw' && !drawingRef.current) {
        const coord = evt.coordinate;
        if (ghostMarkerRef.current) {
          vertexSourceRef.current.removeFeature(ghostMarkerRef.current);
        }
        const ghost = new Feature(new Point(coord));
        ghost.setStyle(ghostMarkerStyle);
        vertexSourceRef.current.addFeature(ghost);
        ghostMarkerRef.current = ghost;
      } else if (ghostMarkerRef.current) {
        vertexSourceRef.current.removeFeature(ghostMarkerRef.current);
        ghostMarkerRef.current = null;
      }
    });

    // --- MAP CLICK HANDLER ---
    map.on('singleclick', evt => {
      if (modeRef.current === 'draw') {
        const coord = evt.coordinate;
        if (!drawingRef.current) {
          if (ghostMarkerRef.current) {
            vertexSourceRef.current.removeFeature(ghostMarkerRef.current);
            ghostMarkerRef.current = null;
          }
          const poly = new Polygon([[coord, coord]]);
          const feature = new Feature(poly);
          feature.setId(Date.now().toString());
          feature.setStyle(activePolygonStyle);
          vectorSource.addFeature(feature);
          polygonFeatureRef.current = feature;
          drawingRef.current = true;
          // Track action in history
          console.log({ FeatureId: feature.getId() });
          setHistory(prev => [
            ...prev,
            {
              type: 'add-point',
              featureId: feature.getId(),
              prevCoords: [],
              newCoords: [[coord, coord]],
            }
          ]);
        } else {
          const feature = polygonFeatureRef.current;
          const poly = feature.getGeometry();
          let coords = poly.getCoordinates()[0];
          const prevCoords = [...coords];
          coords = [...coords.slice(0, -1), coord, coords[coords.length - 1]];
          poly.setCoordinates([coords]);
          console.log(coords.length);
          if (coords.length > 1) {
            createSegmentOverlays(feature, [coords], mapRef);
          }

          // Track action in history
          setHistory(prev => [
            ...prev,
            {
              type: 'add-point',
              featureId: feature.getId(),
              prevCoords,
              newCoords: coords,
            }
          ]);
        }
      }
    });

    function addSegment(direction) {
      if (!drawingRef.current) return;
      const feature = polygonFeatureRef.current;
      const poly = feature.getGeometry();
      let coords = poly.getCoordinates()[0];
      const last = coords.length === 1 ? coords[0] : coords[coords.length - 2];
      let dx = 0, dy = 0;
      const OFFSET = offsetRef.current;
      switch (direction) {
        case 'ArrowUp': dy = OFFSET; break;
        case 'ArrowDown': dy = -OFFSET; break;
        case 'ArrowLeft': dx = -OFFSET; break;
        case 'ArrowRight': dx = OFFSET; break;
        default: return;
      }
      const newPt = [last[0] + dx, last[1] + dy];
      const prevCoords = [...coords];
      coords = [...coords.slice(0, -1), newPt, coords[coords.length - 1]];
      poly.setCoordinates([coords]);
      createSegmentOverlays(feature, [coords], mapRef);

      // Track action in history
      setHistory(prev => [
        ...prev,
        {
          type: 'add-point',
          featureId: feature.getId(),
          prevCoords,
          newCoords: coords,
        }
      ]);
    }

    function handleUndo() {
      setHistory(prev => {
        if (prev.length === 0) return prev;
        const lastAction = prev[prev.length - 1];

        // Find the feature by ID
        let feature = null;
        if (lastAction.featureId) {
          feature = vectorSourceRef.current.getFeatures().find(f => f.getId() === lastAction.featureId);
          if (!feature && polygonFeatureRef.current) feature = polygonFeatureRef.current;
        } else if (polygonFeatureRef.current) {
          feature = polygonFeatureRef.current;
        }
        if (!feature) return prev.slice(0, -1);

        const prevCoords = lastAction.prevCoords;

        // Undo for deleting a segment (LineString -> Polygon)
        if (lastAction.type === 'delete-segment') {
          // Remove the current feature (should be the LineString)
          clearPolygonOverlays(feature, mapRef);
          vectorSourceRef.current.removeFeature(feature);

          // Create a new Polygon feature with the previous coordinates
          const newPoly = new Feature(new Polygon([prevCoords]));
          if (feature.getId) newPoly.setId(feature.getId());
          vectorSourceRef.current.addFeature(newPoly);
          polygonFeatureRef.current = newPoly;

          if (prevCoords && prevCoords.length > 1) {
            createSegmentOverlays(newPoly, [prevCoords], mapRef);
          }
          return prev.slice(0, -1);
        }

        // If undoing would result in no points or just one, remove the feature and reset drawing state
        if (!prevCoords || prevCoords.length <= 1) {
          clearPolygonOverlays(feature, mapRef);
          vectorSourceRef.current.removeFeature(feature);
          polygonFeatureRef.current = null;
          drawingRef.current = false;
          return prev.slice(0, -1);
        }

        // Otherwise, restore previous geometry
        feature.getGeometry().setCoordinates([prevCoords]);
        if (prevCoords.length > 1) {
          createSegmentOverlays(feature, [prevCoords], mapRef);
        }

        return prev.slice(0, -1);
      });
    }

    function handleKeyDown(e) {
      if (
        e.key === 'ArrowUp' ||
        e.key === 'ArrowDown' ||
        e.key === 'ArrowLeft' ||
        e.key === 'ArrowRight'
      ) {
        if (modeRef.current === 'draw') {
          e.preventDefault();
          addSegment(e.key);
        }
      }
      if (e.key === 'Enter' && drawingRef.current && modeRef.current === 'draw') {
        const feature = polygonFeatureRef.current;
        const poly = feature.getGeometry();
        let coords = poly.getCoordinates()[0];
        if (coords.length > 2) {
          coords[coords.length - 1] = coords[0];
          poly.setCoordinates([coords]);
          feature.setStyle(polygonStyle);
          createSegmentOverlays(feature, [coords], mapRef);
          drawingRef.current = false;
        }
      }
      if (
        (e.ctrlKey || e.metaKey) &&
        (e.key === 'z' || e.key === 'Z')
      ) {
        e.preventDefault();
        handleUndo();
      }
    }

    document.addEventListener('keydown', handleKeyDown);

    // Clean up on unmount
    return () => {
      vectorSource.getFeatures().forEach(f => clearPolygonOverlays(f, mapRef));
      overlaysRef.current.forEach(overlay => map.removeOverlay(overlay));
      allOverlaysRef.current.forEach(overlay => map.removeOverlay(overlay));
      map.setTarget(null);
      document.removeEventListener('keydown', handleKeyDown);
      if (selectInteractionRef.current) {
        map.removeInteraction(selectInteractionRef.current);
        selectInteractionRef.current = null;
      }
    };
  }, []);

  useEffect(() => {
    const val = parseFloat(text);
    if (!isNaN(val) && val > 0) {
      offsetRef.current = val;
    } else {
      offsetRef.current = OFFSET_REF_DEFAULT;
    }
  }, [text]);

  useEffect(() => {
    const map = mapRef.current;
    if (!map) return;

    if (mode !== 'draw' && ghostMarkerRef.current && vertexSourceRef.current) {
      vertexSourceRef.current.removeFeature(ghostMarkerRef.current);
      ghostMarkerRef.current = null;
    }

    if (selectInteractionRef.current) {
      map.removeInteraction(selectInteractionRef.current);
      selectInteractionRef.current = null;
    }

    if (mode === 'select') {
      const select = new Select({
        layers: [vectorLayerRef.current],
        style: selectedPolygonStyle
      });
      select.on('select', e => {
        if (e.selected.length > 0) {
          setSelectedFeature(e.selected[0]);
        } else {
          setSelectedFeature(null);
        }
      });
      map.addInteraction(select);
      selectInteractionRef.current = select;
    } else {
      if (selectedFeature) {
        selectedFeature.setStyle(polygonStyle);
        setSelectedFeature(null);
      }
    }

    return () => {
      if (selectInteractionRef.current) {
        map.removeInteraction(selectInteractionRef.current);
        selectInteractionRef.current = null;
      }
    };
  }, [mode, selectedFeature]);

  return (
    <div>
      <input
        id="dimension-input"
        ref={inputRef}
        type="number"
        min="0.1"
        step="0.1"
        value={text}
        onChange={e => setText(e.target.value)}
        placeholder="Dimension (ft.)"
        autoFocus
      />
      <div id="map-menu-vert">
        <div
          className={`map-button${mode === 'draw' ? ' active' : ''}`}
          onClick={() => setMode('draw')}
        >
          Draw
        </div>
        <div
          className={`map-button${mode === 'select' ? ' active' : ''}`}
          onClick={() => setMode('select')}
        >
          Select
        </div>
      </div>
      <div id="map-menu-horiz">
        <FlipHorizButton selectedFeature={selectedFeature} mapRef={mapRef} />
        <FlipVertButton selectedFeature={selectedFeature} mapRef={mapRef} />
        <div className="map-button">Clone</div>
        <div className="map-button">Curve</div>
        <div className="map-button">QuickShape</div>
      </div>
      <div id="map"></div>
      <RightClick
        mapRef={mapRef}
        vectorSourceRef={vectorSourceRef}
        contextMenu={contextMenu}
        setContextMenu={setContextMenu}
        clearPolygonOverlays={(feature) => clearPolygonOverlays(feature, mapRef)}
        setHistory={setHistory}
      />
      <History history={history} />
    </div>
  );
};

export default App;
