import React, { useEffect, useRef, useState } from 'react';
import 'ol/ol.css';
import Map from 'ol/Map';
import View from 'ol/View';
import TileLayer from 'ol/layer/Tile';
import OSM from 'ol/source/OSM';
import VectorLayer from 'ol/layer/Vector';
import VectorSource from 'ol/source/Vector';
import Feature from 'ol/Feature';
import Polygon from 'ol/geom/Polygon';
import Point from 'ol/geom/Point';
import Overlay from 'ol/Overlay';
import { fromLonLat } from 'ol/proj';
import { getLength } from 'ol/sphere';
import Style from 'ol/style/Style';
import CircleStyle from 'ol/style/Circle';
import Fill from 'ol/style/Fill';
import Stroke from 'ol/style/Stroke';
import LineString from 'ol/geom/LineString';

const vertexStyle = new Style({
  image: new CircleStyle({
    radius: 6,
    fill: new Fill({ color: 'blue' }),
    stroke: new Stroke({ color: 'white', width: 2 }),
  }),
});

const polygonStyle = new Style({
  stroke: new Stroke({
    color: 'blue',
    width: 3,
  }),
  fill: new Fill({
    color: 'rgba(30, 144, 255, 0.1)',
  }),
});

const OFFSET = 2; // map units

const App = () => {
  const [text, setText] = useState('');
  const inputRef = useRef();
  const mapRef = useRef();
  const vectorSourceRef = useRef();
  const vertexSourceRef = useRef();
  const polygonFeatureRef = useRef();
  const [drawing, setDrawing] = useState(false);
  const overlaysRef = useRef([]);

  useEffect(() => {
    const vectorSource = new VectorSource();
    vectorSourceRef.current = vectorSource;
    const vertexSource = new VectorSource();
    vertexSourceRef.current = vertexSource;

    const vectorLayer = new VectorLayer({
      source: vectorSource,
      style: feature => {
        // Only style polygons, not points
        if (feature.getGeometry().getType() === 'Polygon') {
          return polygonStyle;
        }
        return null;
      }
    });
    const vertexLayer = new VectorLayer({
      source: vertexSource,
      style: vertexStyle,
    });

    const map = new Map({
      target: 'map',
      layers: [
        new TileLayer({ source: new OSM() }),
        vectorLayer,
        vertexLayer,
      ],
      view: new View({
        center: fromLonLat([-95.87, 40.84]),
        zoom: 24,
      }),
    });
    mapRef.current = map;

    function clearOverlays() {
      overlaysRef.current.forEach(overlay => map.removeOverlay(overlay));
      overlaysRef.current = [];
    }

    function formatLength(line) {
      const lengthMeters = getLength(line);
      const lengthFeet = lengthMeters * 3.28084;
      return lengthFeet > 5280
        ? (lengthFeet / 5280).toFixed(2) + ' mi'
        : lengthFeet.toFixed(2) + ' ft';
    }

    function createSegmentOverlays(coords) {
      clearOverlays();
      for (let i = 1; i < coords.length; i++) {
        const c1 = coords[i - 1];
        const c2 = coords[i];
        const line = new LineString([c1, c2]);
        const length = formatLength(line);

        const midpoint = [
          (c1[0] + c2[0]) / 2,
          (c1[1] + c2[1]) / 2,
        ];

        const tooltip = document.createElement('div');
        tooltip.className = 'segment-tooltip';
        tooltip.style.background = 'white';
        tooltip.style.border = '1px solid black';
        tooltip.style.padding = '2px 4px';
        tooltip.style.position = 'relative';
        tooltip.style.whiteSpace = 'nowrap';
        tooltip.innerHTML = length;

        const overlay = new Overlay({
          element: tooltip,
          position: midpoint,
          positioning: 'center-center',
          stopEvent: false,
        });
        map.addOverlay(overlay);
        overlaysRef.current.push(overlay);
      }
    }

    function updateVertexMarkers(coords) {
      vertexSource.clear();
      // Always show a marker for every vertex except the closing one (which is a duplicate)
      // If only one point, show it
      if (coords.length === 1) {
        vertexSource.addFeature(new Feature(new Point(coords[0])));
      } else {
        for (let i = 0; i < coords.length - 1; i++) {
          vertexSource.addFeature(new Feature(new Point(coords[i])));
        }
      }
    }

    map.on('singleclick', evt => {
      const coord = evt.coordinate;
      if (!drawing) {
        // Start new polygon
        const poly = new Polygon([[coord, coord]]);
        const feature = new Feature(poly);
        feature.setStyle(polygonStyle);
        vectorSource.clear();
        vectorSource.addFeature(feature);
        polygonFeatureRef.current = feature;
        setDrawing(true);
        updateVertexMarkers([coord]);
        createSegmentOverlays([coord]);
      } else {
        // Add vertex
        const feature = polygonFeatureRef.current;
        const poly = feature.getGeometry();
        let coords = poly.getCoordinates()[0];
        coords = [...coords.slice(0, -1), coord, coords[coords.length - 1]];
        poly.setCoordinates([coords]);
        updateVertexMarkers(coords);
        createSegmentOverlays(coords);
      }
    });

    function addSegment(direction) {
      if (!drawing) return;
      const feature = polygonFeatureRef.current;
      const poly = feature.getGeometry();
      let coords = poly.getCoordinates()[0];
      // If only one point, use it as the last
      const last = coords.length === 1 ? coords[0] : coords[coords.length - 2];
      let dx = 0, dy = 0;
      switch (direction) {
        case 'ArrowUp': dy = OFFSET; break;
        case 'ArrowDown': dy = -OFFSET; break;
        case 'ArrowLeft': dx = -OFFSET; break;
        case 'ArrowRight': dx = OFFSET; break;
        default: return;
      }
      const newPt = [last[0] + dx, last[1] + dy];
      coords = [...coords.slice(0, -1), newPt, coords[coords.length - 1]];
      poly.setCoordinates([coords]);
      updateVertexMarkers(coords);
      createSegmentOverlays(coords);
    }

    function handleKeyDown(e) {
      if (
        e.key === 'ArrowUp' ||
        e.key === 'ArrowDown' ||
        e.key === 'ArrowLeft' ||
        e.key === 'ArrowRight'
      ) {
        e.preventDefault();
        addSegment(e.key);
      }
      // Finish polygon with Enter
      if (e.key === 'Enter' && drawing) {
        const feature = polygonFeatureRef.current;
        const poly = feature.getGeometry();
        let coords = poly.getCoordinates()[0];
        if (coords.length > 2) {
          coords[coords.length - 1] = coords[0];
          poly.setCoordinates([coords]);
          updateVertexMarkers(coords);
          createSegmentOverlays(coords);
          setDrawing(false);
        }
      }
    }

    document.addEventListener('keydown', handleKeyDown);

    return () => {
      map.setTarget(null);
      document.removeEventListener('keydown', handleKeyDown);
    };
  }, [drawing]);

  return (
    <div>
      <input
        ref={inputRef}
        type="text"
        value={text}
        onChange={e => setText(e.target.value)}
        placeholder="Dimension"
        autoFocus
        style={{
          position: 'absolute',
          top: 20,
          right: 20,
          zIndex: 1000,
          width: 300,
          padding: 8,
          fontSize: 16,
          pointerEvents: 'auto',
        }}
      />
      <div id="map" style={{ width: '100vw', height: '100vh' }}></div>
      <style>
        {`
          .segment-tooltip {
            font-size: 12px;
            pointer-events: none;
          }
        `}
      </style>
    </div>
  );
};

export default App;
