import React, { useEffect, useRef, useState } from 'react';
import './style.css';
import 'ol/ol.css';
import Map from 'ol/Map';
import View from 'ol/View';
import TileLayer from 'ol/layer/Tile';
import OSM from 'ol/source/OSM';
import VectorLayer from 'ol/layer/Vector';
import VectorSource from 'ol/source/Vector';
import Feature from 'ol/Feature';
import Polygon from 'ol/geom/Polygon';
import Point from 'ol/geom/Point';
import Overlay from 'ol/Overlay';
import { fromLonLat } from 'ol/proj';
import { getLength } from 'ol/sphere';
import Style from 'ol/style/Style';
import CircleStyle from 'ol/style/Circle';
import Fill from 'ol/style/Fill';
import Stroke from 'ol/style/Stroke';
import LineString from 'ol/geom/LineString';

const vertexStyle = new Style({
  image: new CircleStyle({
    radius: 6,
    fill: new Fill({ color: 'blue' }),
    stroke: new Stroke({ color: 'white', width: 2 }),
  }),
});

const polygonStyle = new Style({
  stroke: new Stroke({
    color: 'blue',
    width: 3,
  }),
  fill: new Fill({
    color: 'rgba(30, 144, 255, 0.1)',
  }),
});

const activePolygonStyle = new Style({
  stroke: new Stroke({
    color: 'orange',
    width: 3,
  }),
  fill: new Fill({
    color: 'rgba(255, 165, 0, 0.1)',
  }),
});

const selectedPolygonStyle = new Style({
  stroke: new Stroke({
    color: 'limegreen',
    width: 3,
    lineDash: [8, 4],
  }),
  fill: new Fill({
    color: 'rgba(50, 205, 50, 0.15)',
  }),
});

const ghostMarkerStyle = new Style({
  image: new CircleStyle({
    radius: 7,
    fill: new Fill({ color: 'rgba(0,0,255,0.2)' }),
    stroke: new Stroke({ color: 'blue', width: 2 }),
  }),
});

const OFFSET_REF_DEFAULT = 2.018; // fallback value

const App = () => {
  const [text, setText] = useState('');
  const [mode, setMode] = useState('draw'); // 'draw' or 'select'
  const [selectedFeature, setSelectedFeature] = useState(null);
  const inputRef = useRef();
  const mapRef = useRef();
  const vectorSourceRef = useRef();
  const vertexSourceRef = useRef();
  const polygonFeatureRef = useRef();
  const overlaysRef = useRef([]);      // overlays for current drawing
  const allOverlaysRef = useRef([]);   // overlays for finished polygons
  const drawingRef = useRef(false);
  const offsetRef = useRef(OFFSET_REF_DEFAULT);
  const ghostMarkerRef = useRef(null);

  // --- ComponentDidMount: OpenLayers setup, only runs once ---
  useEffect(() => {
    const vectorSource = new VectorSource();
    vectorSourceRef.current = vectorSource;
    const vertexSource = new VectorSource();
    vertexSourceRef.current = vertexSource;

    const vectorLayer = new VectorLayer({
      source: vectorSource,
      style: polygonStyle,
    });
    const vertexLayer = new VectorLayer({
      source: vertexSource,
      style: vertexStyle,
    });

    const map = new Map({
      target: 'map',
      layers: [
        new TileLayer({ source: new OSM() }),
        vectorLayer,
        vertexLayer,
      ],
      view: new View({
        center: fromLonLat([-95.87, 40.84]),
        zoom: 24,
      }),
    });
    mapRef.current = map;

    function clearCurrentOverlays() {
      overlaysRef.current.forEach(overlay => {
        if (overlay instanceof Overlay) {
          mapRef.current.removeOverlay(overlay);
        }
      });
      overlaysRef.current = [];
    }

    function formatLength(line) {
      const lengthMeters = getLength(line);
      const lengthFeet = lengthMeters * 3.28084;
      return lengthFeet > 5280
        ? (lengthFeet / 5280).toFixed(2) + ' mi'
        : lengthFeet.toFixed(2) + "'";
    }

    function createSegmentOverlays(coords) {
      clearCurrentOverlays();
      const DIM_OFFSET = 17; // pixels to offset overlays and dimension lines

      for (let i = 1; i < coords.length; i++) {
        const c1 = coords[i - 1];
        const c2 = coords[i];
        const line = new LineString([c1, c2]);
        const length = formatLength(line);

        // Calculate perpendicular offset direction (in screen pixels)
        const pixel1 = mapRef.current.getPixelFromCoordinate(c1);
        const pixel2 = mapRef.current.getPixelFromCoordinate(c2);
        const dx = pixel2[0] - pixel1[0];
        const dy = pixel2[1] - pixel1[1];
        const len = Math.sqrt(dx * dx + dy * dy);
        // Perpendicular vector (normalized)
        const perp = len === 0 ? [0, 0] : [(dy / len), -(dx / len)];

        // Offset midpoint in screen pixels
        const offsetMidPixel = [
          (pixel1[0] + pixel2[0]) / 2 + perp[0] * DIM_OFFSET,
          (pixel1[1] + pixel2[1]) / 2 + perp[1] * DIM_OFFSET,
        ];
        // Convert back to map coordinates
        const offsetMidCoord = mapRef.current.getCoordinateFromPixel(offsetMidPixel);

        // Place the tooltip at the offset position
        const tooltip = document.createElement('div');
        tooltip.className = 'segment-tooltip';
        tooltip.innerHTML = length;

        // Rotate the tooltip if the segment is more vertical than horizontal
        const isVertical = Math.abs(dx) < Math.abs(dy) * 0.5;
        if (isVertical) {
          tooltip.style.transform = 'rotate(270deg)';
        }

        const overlay = new Overlay({
          element: tooltip,
          position: offsetMidCoord,
          positioning: 'center-center',
          stopEvent: false,
        });
        mapRef.current.addOverlay(overlay);
        overlaysRef.current.push(overlay);
      }
    }

    function addVertexMarkers(coords) {
      for (let i = 0; i < coords.length - 1; i++) {
        vertexSource.addFeature(new Feature(new Point(coords[i])));
      }
    }

    function updateVertexMarkers(coords) {
      // Remove only the temporary markers for the current drawing
      const toRemove = [];
      vertexSource.getFeatures().forEach(f => {
        if (f.get('temp')) toRemove.push(f);
      });
      toRemove.forEach(f => vertexSource.removeFeature(f));
      for (let i = 0; i < coords.length - 1; i++) {
        const feat = new Feature(new Point(coords[i]));
        feat.set('temp', true);
        vertexSource.addFeature(feat);
      }
      if (coords.length === 1) {
        const feat = new Feature(new Point(coords[0]));
        feat.set('temp', true);
        vertexSource.addFeature(feat);
      }
    }

    // --- GHOST MARKER LOGIC ---
    map.on('pointermove', evt => {
      if (mode === 'draw' && !drawingRef.current) {
        const coord = evt.coordinate;
        // Remove previous ghost marker
        if (ghostMarkerRef.current) {
          vertexSourceRef.current.removeFeature(ghostMarkerRef.current);
        }
        // Add new ghost marker
        const ghost = new Feature(new Point(coord));
        ghost.setStyle(ghostMarkerStyle);
        vertexSourceRef.current.addFeature(ghost);
        ghostMarkerRef.current = ghost;
      } else if (ghostMarkerRef.current) {
        // Remove ghost marker if not in draw mode or drawing started
        vertexSourceRef.current.removeFeature(ghostMarkerRef.current);
        ghostMarkerRef.current = null;
      }
    });

    // --- MAP CLICK HANDLER ---
    map.on('singleclick', evt => {
      if (mode === 'draw') {
        const coord = evt.coordinate;
        if (!drawingRef.current) {
          // Remove ghost marker
          if (ghostMarkerRef.current) {
            vertexSourceRef.current.removeFeature(ghostMarkerRef.current);
            ghostMarkerRef.current = null;
          }
          // Start new polygon
          const poly = new Polygon([[coord, coord]]);
          const feature = new Feature(poly);
          feature.setStyle(activePolygonStyle); // Active style
          vectorSource.addFeature(feature);
          polygonFeatureRef.current = feature;
          drawingRef.current = true;
          updateVertexMarkers([coord, coord]);
          createSegmentOverlays([coord, coord]);
        } else {
          // Add vertex
          const feature = polygonFeatureRef.current;
          const poly = feature.getGeometry();
          let coords = poly.getCoordinates()[0];
          coords = [...coords.slice(0, -1), coord, coords[coords.length - 1]];
          poly.setCoordinates([coords]);
          updateVertexMarkers(coords);
          createSegmentOverlays(coords);
        }
      } else if (mode === 'select') {
        // Selection mode: select polygon on click
        let selected = null;
        map.forEachFeatureAtPixel(evt.pixel, function (feature) {
          if (feature.getGeometry() instanceof Polygon) {
            selected = feature;
            return true;
          }
          return false;
        });
        // Remove previous selection style
        if (selectedFeature && selectedFeature !== selected) {
          selectedFeature.setStyle(polygonStyle);
        }
        if (selected) {
          selected.setStyle(selectedPolygonStyle);
          setSelectedFeature(selected);
        } else if (selectedFeature) {
          selectedFeature.setStyle(polygonStyle);
          setSelectedFeature(null);
        }
      }
    });

    function addSegment(direction) {
      if (!drawingRef.current) return;
      const feature = polygonFeatureRef.current;
      const poly = feature.getGeometry();
      let coords = poly.getCoordinates()[0];
      const last = coords.length === 1 ? coords[0] : coords[coords.length - 2];
      let dx = 0, dy = 0;
      // Use offsetRef.current instead of OFFSET
      const OFFSET = offsetRef.current;
      switch (direction) {
        case 'ArrowUp': dy = OFFSET; break;
        case 'ArrowDown': dy = -OFFSET; break;
        case 'ArrowLeft': dx = -OFFSET; break;
        case 'ArrowRight': dx = OFFSET; break;
        default: return;
      }
      const newPt = [last[0] + dx, last[1] + dy];
      coords = [...coords.slice(0, -1), newPt, coords[coords.length - 1]];
      poly.setCoordinates([coords]);
      updateVertexMarkers(coords);
      createSegmentOverlays(coords);
    }

    function handleKeyDown(e) {
      if (
        e.key === 'ArrowUp' ||
        e.key === 'ArrowDown' ||
        e.key === 'ArrowLeft' ||
        e.key === 'ArrowRight'
      ) {
        if (mode === 'draw') {
          e.preventDefault();
          addSegment(e.key);
        }
      }
      // Finish polygon with Enter
      if (e.key === 'Enter' && drawingRef.current && mode === 'draw') {
        const feature = polygonFeatureRef.current;
        const poly = feature.getGeometry();
        let coords = poly.getCoordinates()[0];
        if (coords.length > 2) {
          coords[coords.length - 1] = coords[0];
          poly.setCoordinates([coords]);
          feature.setStyle(polygonStyle); // Set finished style
          updateVertexMarkers([]); // remove temp
          addVertexMarkers(coords); // add permanent
          createSegmentOverlays(coords);
          // Move overlays to allOverlaysRef so they persist
          allOverlaysRef.current.push(...overlaysRef.current);
          overlaysRef.current = [];
          drawingRef.current = false;
        }
      }
      // Undo last vertex with Ctrl+Z
      if (
        (e.ctrlKey || e.metaKey) &&
        (e.key === 'z' || e.key === 'Z') &&
        drawingRef.current &&
        mode === 'draw'
      ) {
        const feature = polygonFeatureRef.current;
        const poly = feature.getGeometry();
        let coords = poly.getCoordinates()[0];
        if (coords.length > 2) {
          // Remove the second-to-last coordinate (last is closing point)
          coords.splice(coords.length - 2, 1);
          poly.setCoordinates([coords]);
          updateVertexMarkers(coords);
          createSegmentOverlays(coords);
        }
      }
    }

    document.addEventListener('keydown', handleKeyDown);

    // Clean up on unmount
    return () => {
      overlaysRef.current.forEach(overlay => map.removeOverlay(overlay));
      allOverlaysRef.current.forEach(overlay => map.removeOverlay(overlay));
      map.setTarget(null);
      document.removeEventListener('keydown', handleKeyDown);
    };
    // eslint-disable-next-line
  }, []); // Only run once on mount

  // --- Update offsetRef when [text] changes ---
  useEffect(() => {
    const val = parseFloat(text);
    if (!isNaN(val) && val > 0) {
      offsetRef.current = val;
    } else {
      offsetRef.current = OFFSET_REF_DEFAULT;
    }
  }, [text]);

  // --- Mode and selection logic (no map/layer recreation) ---
  useEffect(() => {
    // Remove ghost marker if switching out of draw mode
    if (mode !== 'draw' && ghostMarkerRef.current && vertexSourceRef.current) {
      vertexSourceRef.current.removeFeature(ghostMarkerRef.current);
      ghostMarkerRef.current = null;
    }
    // Remove selection style if switching out of select mode
    if (mode !== 'select' && selectedFeature) {
      selectedFeature.setStyle(polygonStyle);
      setSelectedFeature(null);
    }
  }, [mode]);

  return (
    <div>
      <input
        id="dimension-input"
        ref={inputRef}
        type="number"
        min="0.1"
        step="0.1"
        value={text}
        onChange={e => setText(e.target.value)}
        placeholder="Dimension (ft.)"
        autoFocus
      />
      <div id="map-menu-vert">
        <div
          className={`map-button${mode === 'draw' ? ' active' : ''}`}
          onClick={() => setMode('draw')}
        >
          Draw
        </div>
        <div
          className={`map-button${mode === 'select' ? ' active' : ''}`}
          onClick={() => setMode('select')}
        >
          Select
        </div>
      </div>
      <div id="map-menu-horiz">
        <div className="map-button">Clone</div>
        <div className="map-button">FlipHoriz</div>
        <div className="map-button">FlipVert</div>
        <div className="map-button">Curve</div>
        <div className="map-button">QuickShape</div>
      </div>
      <div id="map" style={{ width: '100vw', height: '100vh' }}></div>
    </div>
  );
};

export default App;
