import React, { useState, useRef, useEffect } from "react";
import { QuickShapeIcon } from "../svg";
import Draw from "ol/interaction/Draw";
import Polygon, { fromCircle } from "ol/geom/Polygon";

const SHAPES = [
  { name: "Circle", value: "circle" },
  { name: "Triangle", value: "triangle" },
  { name: "Square", value: "square" },
];

export default function QuickShape({ mapRef, vectorSourceRef, setDrawing, setMode, addHistoryAction }) {
  const [open, setOpen] = useState(false);
  const menuRef = useRef();
  const drawRef = useRef(null);

  // Close menu if clicked outside
  useEffect(() => {
    function handleClickOutside(e) {
      if (menuRef.current && !menuRef.current.contains(e.target)) {
        setOpen(false);
      }
    }
    if (open) {
      document.addEventListener("mousedown", handleClickOutside);
    }
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, [open]);

  // Cleanup draw interaction on unmount
  useEffect(() => {
    const map = mapRef.current; // capture the current map instance

    return () => {
      if (drawRef.current && map) {
        map.removeInteraction(drawRef.current);
        drawRef.current = null;
      }
    };
  }, [mapRef]);

  function handleShapeClick(shape) {
    setOpen(false);
    if (shape.value === "circle" && mapRef.current && vectorSourceRef.current) {
      // Remove any existing draw interaction
      if (drawRef.current) {
        mapRef.current.removeInteraction(drawRef.current);
        drawRef.current = null;
      }
      // Add new draw interaction for circle
      const draw = new Draw({
        source: vectorSourceRef.current,
        type: "Circle",
      });
      mapRef.current.addInteraction(draw);
      drawRef.current = draw;
      setDrawing(true);
      setMode("circle");

      draw.on("drawend", (evt) => {
        const circleGeom = evt.feature.getGeometry();
        const polygonGeom = fromCircle(circleGeom, 64);
        evt.feature.setGeometry(polygonGeom);

        // Ensure the feature has an ID for undo/redo
        if (!evt.feature.getId()) {
          evt.feature.setId(Date.now().toString());
        }

        addHistoryAction({
          type: "draw-circle",
          featureId: evt.feature.getId(),
          coords: polygonGeom.getCoordinates(),
        });

        setDrawing(false);
        setMode("select");
        if (drawRef.current) {
          mapRef.current.removeInteraction(drawRef.current);
          drawRef.current = null;
        }
      });
    }
    // You can add similar logic for triangle/square here in the future
  }

  return (
    <div ref={menuRef} style={{ position: "relative", display: "inline-block" }}>
      <button
        title="Quick Shape"
        className="map-button"
        onClick={() => setOpen((v) => !v)}
        style={{ display: "flex", alignItems: "center", justifyContent: "center" }}
      >
        <QuickShapeIcon style={{ width: 24, height: 24 }} />
      </button>
      {open && (
        <div
          style={{
            position: "absolute",
            top: "50%",
            left: "110%",
            transform: "translateY(-50%)",
            background: "#fff",
            border: "1px solid #ccc",
            borderRadius: 4,
            boxShadow: "0 2px 8px rgba(0,0,0,0.15)",
            zIndex: 10,
            padding: 4,
            display: "flex",
            flexDirection: "column",
            alignItems: "center"
          }}
        >
          {SHAPES.map((shape) => (
            <div
              key={shape.value}
              style={{
                position: "relative",
                width: "60%",
                padding: "6px 12px",
                cursor: "pointer",
                textAlign: "center",
                borderRadius: 3,
                margin: 2,
                transition: "background 0.2s",
              }}
              className="quickshape-item"
              onClick={() => handleShapeClick(shape)}
              onMouseDown={e => e.preventDefault()} // Prevents button blur
            >
              {shape.name}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
