import React from 'react';

// Helper to calculate Euclidean distance in feet
function segmentLengthFeet(a, b) {
  const dx = a[0] - b[0];
  const dy = a[1] - b[1];
  const meters = Math.sqrt(dx * dx + dy * dy);
  return meters * 3.28084;
}

const Edit = ({ feature, onSegmentHover, onSegmentUnhover }) => {
  if (!feature) return null;
  const coords = feature.getGeometry().getCoordinates()[0];
  const N = coords.length - 1;

  return (
    <div
      style={{
        position: 'absolute',
        top: 85,
        right: 20,
        width: 225,
        height: 'calc(100vh - 150px)',
        display: 'flex',
        flexDirection: 'column',
        background: 'rgba(255,255,255,0.97)',
        borderLeft: '1px solid #ccc',
        boxShadow: '-2px 0 8px rgba(0,0,0,0.07)',
        zIndex: 2000,
        fontSize: 13,
        padding: 12,
        overflow: 'hidden',
      }}
    >
      <h3 style={{ marginTop: 0 }}>Edit Segments</h3>
      <ol style={{ paddingLeft: 20, overflowY: 'auto', flex: 1, minHeight: 0 }}>
        {Array.from({ length: N }).map((_, i) => {
          const a = coords[i];
          const b = coords[(i + 1) % N];
          const lengthFeet = segmentLengthFeet(a, b);
          return (
            <li
              key={i}
              className="edit-segment-item"
              style={{ marginBottom: 10, borderRadius: 4, transition: 'background 0.2s', cursor: 'pointer' }}
              onMouseEnter={() => onSegmentHover(i)}
              onMouseLeave={onSegmentUnhover}
            >
              <strong>Segment {i + 1}:</strong> {lengthFeet.toFixed(2)} ft
            </li>
          );
        })}
      </ol>
    </div>
  );
};

export default Edit;
