import React from 'react';

const Modal = ({ message, open, onClose }) => {
  if (!open) return null;

  return (
    <div
      style={{
        position: 'fixed',
        zIndex: 3000,
        top: 0,
        left: 0,
        width: '100vw',
        height: '100vh',
        background: 'rgba(0,0,0,0.35)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
      }}
      onClick={onClose}
    >
      <div
        style={{
          background: '#fff',
          borderRadius: 2,
          boxShadow: '0 4px 24px rgba(0,0,0,0.18)',
          padding: '32px 28px 20px 28px',
          minWidth: 320,
          maxWidth: '80vw',
          textAlign: 'center',
          position: 'relative'
        }}
        onClick={e => e.stopPropagation()}
      >
        <div style={{ fontSize: 17, marginBottom: 24 }}>{message}</div>
        <button
          style={{
            padding: '8px 24px',
            fontSize: 15,
            borderRadius: 4,
            border: 'none',
            background: '#1976d2',
            color: '#fff',
            cursor: 'pointer'
          }}
          onClick={onClose}
        >
          OK
        </button>
      </div>
    </div>
  );
};

export default Modal;
