import React, { useEffect } from 'react';
import { Feature } from 'ol';
import LineString from 'ol/geom/LineString';

// Props: 
// - mapRef, vectorSourceRef, contextMenu, setContextMenu, clearPolygonOverlays
const RightClick = ({
  mapRef,
  vectorSourceRef,
  contextMenu,
  setContextMenu,
  clearPolygonOverlays
}) => {
  // Attach right-click handler to map div
  useEffect(() => {
    function handleCustomContextMenu(e) {
      const mapDiv = document.getElementById('map');
      if (!mapDiv || !mapRef.current || !vectorSourceRef.current) return;
      if (!mapDiv.contains(e.target)) return; // Only handle right-clicks inside the map
      e.preventDefault();
      const pixel = mapRef.current.getEventPixel(e);
      const features = vectorSourceRef.current.getFeatures();

      // Check for vertex
      let found = false;
      for (const feature of features) {
        const geom = feature.getGeometry();
        if (geom.getType() === 'Polygon') {
          const coords = geom.getCoordinates()[0];
          for (let i = 0; i < coords.length - 1; i++) {
            const coordPixel = mapRef.current.getPixelFromCoordinate(coords[i]);
            if (Math.hypot(pixel[0] - coordPixel[0], pixel[1] - coordPixel[1]) < 8) {
              setContextMenu({ visible: true, x: e.clientX, y: e.clientY, type: 'vertex', feature, index: i });
              found = true;
              break;
            }
          }
        }
        if (found) break;
      }

      // If not vertex, check for segment
      if (!found) {
        for (const feature of features) {
          const geom = feature.getGeometry();
          if (geom.getType() === 'Polygon') {
            const coords = geom.getCoordinates()[0];
            for (let i = 0; i < coords.length - 1; i++) {
              const c1 = mapRef.current.getPixelFromCoordinate(coords[i]);
              const c2 = mapRef.current.getPixelFromCoordinate(coords[i + 1]);
              const t = ((pixel[0] - c1[0]) * (c2[0] - c1[0]) + (pixel[1] - c1[1]) * (c2[1] - c1[1])) /
                ((c2[0] - c1[0]) ** 2 + (c2[1] - c1[1]) ** 2);
              if (t >= 0 && t <= 1) {
                const proj = [c1[0] + t * (c2[0] - c1[0]), c1[1] + t * (c2[1] - c1[1])];
                if (Math.hypot(pixel[0] - proj[0], pixel[1] - proj[1]) < 8) {
                  setContextMenu({ visible: true, x: e.clientX, y: e.clientY, type: 'segment', feature, index: i });
                  found = true;
                  break;
                }
              }
            }
          }
          if (found) break;
        }
      }

      // If not found, show menu with no actions
      if (!found) setContextMenu({ visible: true, x: e.clientX, y: e.clientY, type: null, feature: null, index: null });
    }

    document.addEventListener('contextmenu', handleCustomContextMenu);
    return () => document.removeEventListener('contextmenu', handleCustomContextMenu);
  }, [mapRef, vectorSourceRef, setContextMenu]);

  // Hide menu on click elsewhere
  useEffect(() => {
    function hideMenu() {
      setContextMenu(menu => menu.visible ? { ...menu, visible: false } : menu);
    }
    window.addEventListener('click', hideMenu);
    return () => window.removeEventListener('click', hideMenu);
  }, [setContextMenu]);

  // Menu action handler
  const handleMenuAction = (action) => {
    if (!contextMenu.feature) return;
    const geom = contextMenu.feature.getGeometry();
    let coords = geom.getCoordinates()[0].map(c => [c[0], c[1]]);

    if (action === 'delete-vertex' && coords.length > 3) {
      coords.splice(contextMenu.index, 1);
      coords[coords.length - 1] = coords[0];
      geom.setCoordinates([coords]);
      if (mapRef.current && mapRef.current.remarkPolygonSegments) {
        mapRef.current.remarkPolygonSegments(contextMenu.feature);
      }
      setContextMenu({ ...contextMenu, visible: false });
    }

    if (action === 'delete-segment' && coords.length > 3) {
      // Remove the closing coordinate if present
      if (
        coords.length > 2 &&
        coords[0][0] === coords[coords.length - 1][0] &&
        coords[0][1] === coords[coords.length - 1][1]
      ) {
        coords = coords.slice(0, coords.length - 1);
      }
      const index = contextMenu.index;
      const newCoords = [
        ...coords.slice(0, index + 1),
        ...coords.slice(index + 2)
      ];
      // Remove overlays for the old feature
      if (clearPolygonOverlays) clearPolygonOverlays(contextMenu.feature);
      // Remove the polygon feature and add a LineString feature
      // if (vectorSourceRef.current.hasFeature(contextMenu.feature)) {
      //   vectorSourceRef.current.removeFeature(contextMenu.feature);
      // }
      if (vectorSourceRef.current && vectorSourceRef.current.getFeatures().includes(contextMenu.feature)) {
        vectorSourceRef.current.removeFeature(contextMenu.feature);
      }
      const lineFeature = new Feature(new LineString(newCoords));
      vectorSourceRef.current.addFeature(lineFeature);
      if (mapRef.current && mapRef.current.remarkPolygonSegments) {
        mapRef.current.remarkPolygonSegments(lineFeature);
      }
      setContextMenu({ ...contextMenu, visible: false });
    }
  };

  // Render the context menu
  return contextMenu.visible ? (
    <div className="right-click-menu"
      style={{
        position: 'fixed',
        top: contextMenu.y,
        left: contextMenu.x,
        background: '#fff',
        border: '1px solid #ccc',
        zIndex: 1000,
        padding: 4,
        minWidth: 120
      }}
      onContextMenu={e => e.preventDefault()}
      onMouseLeave={() => setContextMenu({ ...contextMenu, visible: false })}
    >
      {contextMenu.type === 'vertex' && (
        <div
          className="menu-item"
          onClick={() => handleMenuAction('delete-vertex')}
          style={{ cursor: 'pointer', padding: '4px 8px' }}
        >
          Delete Vertex
        </div>
      )}
      {contextMenu.type === 'segment' && (
        <div
          className="menu-item"
          onClick={() => handleMenuAction('delete-segment')}
          style={{ cursor: 'pointer', padding: '4px 8px' }}
        >
          Delete Segment
        </div>
      )}
      {!contextMenu.type && <div style={{ color: '#888', padding: '4px 8px' }}>No actions</div>}
    </div>
  ) : null;
};

export default RightClick;
